package com.example.vistas5

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.core.widget.addTextChangedListener
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.vistas5.adapter.ComidaMascotaAdapter
import com.example.vistas5.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding
    private var comidaMascotaMutableList:MutableList<comidaMascota> = ComidaMascotaProvider.comidaMascotaList.toMutableList()
    private lateinit var adapter: ComidaMascotaAdapter
    private val llManager = LinearLayoutManager(this)

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)
        binding.btnAddComida.setOnClickListener {agregarAlimento()}
        binding.etFiltrar.addTextChangedListener { userFilter ->

        val comidaMascotaFilter = comidaMascotaMutableList.filter { alimentoMascota ->
            alimentoMascota.marca.lowercase().contains(userFilter.toString().lowercase())
        }
        adapter.updateComida(comidaMascotaFilter)
        }
        initRecicleView()
    }

    private fun agregarAlimento() {
        val alimentoMascota = comidaMascota(
            "¿Mascota?",
            "¿Edad?",
            "¿Marca?",
            "data:image/jpeg;base64,"
        )
    comidaMascotaMutableList.add(index = 5, alimentoMascota)
        adapter.notifyItemInserted(5)
        llManager.scrollToPositionWithOffset(5, 50)
    }

    private fun initRecicleView() {
        adapter = ComidaMascotaAdapter(
        comidamascotaList = comidaMascotaMutableList,
        onClickListener = { comidaMascota -> onItemSelected(comidaMascota) },
        onClickDelete = {position -> onDeletedItem(position)}
        )

        binding.recyclerasd.layoutManager = llManager
        binding.recyclerasd.adapter = adapter

    }
    private fun onDeletedItem (position:Int){
        comidaMascotaMutableList.removeAt(position)
        adapter.notifyItemRemoved(position)
    }

    private fun onItemSelected(comidaMascota:comidaMascota){

        Toast.makeText(this, comidaMascota.marca, Toast.LENGTH_SHORT).show()
        
    }

}